import React, {Component} from 'react';
import './App.css';
import './slideshow.css';
import logo from './logo.jpg';
import { Slide } from 'react-slideshow-image';
import img1 from './images/IMG_8920.jpg';


class Header extends Component {  

  render() {

    return (
      <div className="Header">
        <div className="logo-container"><img className="logo" src={logo} alt="Logo" /></div>
        <Slideshow />
      </div>

    )
  }

}

export default Header;

const slideImages = [
  require('./images/IMG_8920.jpg'),
  require('./images/IMG_9408.jpg')
];

const properties = {
  duration: 5000,
  transitionDuration: 500,
  infinite: true,
  indicators: true,
  arrows: true,
  onChange: (oldIndex, newIndex) => {
    console.log(`slide transition from ${oldIndex} to ${newIndex}`);
  }
}

const Slideshow = () => {
  return (
    <div className="slide-container">
      <Slide {...properties}>
        <div className="each-slide">
          <div style={{'backgroundImage': 'url(' + require('./images/IMG_8920.jpg') + ')'}}>
          </div>
        </div>
        <div className="each-slide">
          <div style={{'backgroundImage': `url(${slideImages[1]})`}}>
          </div>
        </div>
      </Slide>
    </div>
  )
}
